format long;n = [8, 64, 128];f = @(x) e^x;g = @(x) cos(x);fun = @(x,y) e^y * cos(x);real_val = dblquad(fun, 0 , pi/2, 0 , 1);Result = [];for i = 1:length(n)  tic;  [res_f, err_f, eff_f] = romberg(f, 0, 1 , n(i));  [res_g, err_g, eff_g] = romberg(g, 0, pi/2, n(i));  time = toc;  res = res_f * res_g;  err = err_f * err_g;  eff = eff_f + eff_g;  accuracy = abs(real_val - res);  Result = [Result; i, time, res, err, eff, accuracy];endforResult